		_, err := postgresql.NewGrantRole(ctx, "grantRoot", &postgresql.GrantRoleArgs{
			GrantRole:       pulumi.String("application"),
			Role:            pulumi.String("root"),
			WithAdminOption: pulumi.Bool(true),
		})
		if err != nil {
			return err
